<?phpclass Banner_model extends CI_Model {	public function get_banner_list($language_id = NULL)	{			$result = NULL;				$query = $this				->db				->select('banner_id, banner_name, web_banner, web_banner_width, web_banner_height, web_banner_alt, mobile_banner, mobile_banner_width, mobile_banner_height, mobile_banner_alt, banner_url, banner_sequence, language_id')				->where('language_id', $language_id)				->where('active', STATUS_ACTIVE)				->order_by('banner_sequence', 'ASC')				->get('banners');				if($query->num_rows() > 0)		{			$result = $query->result_array();			}				$query->free_result();				return $result;	}		public function get_banner_data($id = NULL)	{			$result = NULL;				$query = $this				->db				->select('banner_id, banner_name, web_banner, web_banner_width, web_banner_height, web_banner_alt, mobile_banner, mobile_banner_width, mobile_banner_height, mobile_banner_alt, banner_url, banner_sequence, language_id')				->where('banner_id', $id)				->where('active', STATUS_ACTIVE)				->limit(1)				->get('banners');				if($query->num_rows() > 0)		{			$result = $query->row_array();  		}				$query->free_result();				return $result;	}}