<?phpclass Contact_model extends CI_Model {	public function get_contact_list()	{			$result = NULL;				$query = $this				->db				->select('contact_id, im_name, im_value')				->where('active', STATUS_ACTIVE)				->get('contacts');				if($query->num_rows() > 0)		{			$result = $query->result_array();			}				$query->free_result();				return $result;	}		public function get_contact_data($id = NULL)	{			$result = NULL;				$query = $this				->db				->select('contact_id, im_name, im_value')				->where('contact_id', $id)				->where('active', STATUS_ACTIVE)				->limit(1)				->get('contacts');				if($query->num_rows() > 0)		{			$result = $query->row_array();  		}				$query->free_result();				return $result;	}}